
public class Indexof_1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//010-1234-5678
		String str = "010-1234-5678";
		//String str [] = {"010-1234-5678"};
		 
		
		int a = str.indexOf('-');
		System.out.println(a);
		
		for(int i=0; i<=2;i++);
	
		String str1 = str.substring(a,a+1);
		System.out.println(str1);
		
		
			
		System.out.println(a);
	}

}
