
public class Int_test {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 10;
		int j = 20;
		double k = (double)i;
		double l = (double)j;
		
		int result_1 = i * j;
		int result_2 = i / j;
		double result_3 = k / l;
		System.out.println("결과 1 :"+ result_1);
		System.out.println("결과 2 :"+ result_2);
		System.out.println("결과 3 :"+ result_3);
	}
}
