
public class Var_change {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int int_val = 66;
		char char_val = (char)int_val;
		
		System.out.println("val :"+ char_val);
		
		long long_val = 500L;
		int_val = (int)long_val;
		System.out.println("val2 :"+int_val);
		
	}

}
